package smartbatch;

public class Lab22 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int n;
		n = Integer.parseInt(args[0]);
		if(n>0)
		{
			System.out.println("Positive");
		}
		else if(n<0)
		{
			System.out.println("negative");
		}
		else
		{
			System.out.println("zero");
		}
		
		
	}
}
